import { defer, json, redirect, useRouteLoaderData } from 'react-router-dom';
import EventItem from '../components/EventItem';

const EventDetailPage = () => {
	const data = useRouteLoaderData('event-detail');

	return <EventItem event={data.event} />;
};
export default EventDetailPage;

const loadEvent = async id => {
	const response = await fetch(`http://localhost:8080/events/${id}`);

	if (!response.ok) {
		throw json(
			{
				message: `Could not fetch details for selected event ${id}.`
			},
			{ status: 500 }
		);
	}

	const data = await response.json();
	return data.event;
};

const loadEvents = async () => {
	const response = await fetch('http://localhost:8080/events');

	if (!response.ok) {
		// return { isError: true, message: 'Could not fetch events.' };
		// throw new Response(JSON.stringify({ message: 'Could not fetch events.' }), {
		// 	status: 500
		// });
		return json({ message: 'Could not fetch events.' }, { status: 500 });
	}
	const data = await response.json();
	return data.events;
};

export const loader = async ({ request, params }) => {
	const id = params.eventId;

	return defer({ event: loadEvent(id), events: loadEvents() });
};

export const action = async ({ request, params }) => {
	const id = params.eventId;
	const response = await fetch(`http://localhost:8080/events/${id}`, {
		method: request.method
	});
	if (!response.ok) {
		throw json(
			{
				message: `Could not delete event ${id}.`
			},
			{ status: 500 }
		);
	}

	return redirect('/events');
};
